mapper.select:

select count(*) as count, m_name, m_grade from members
where m_id=#{id} and AES_DECRYPT(unhex(m_pw),'bae')=#{pw}
and m_grade > 5

select ncontent
	, nrealfile
from notice where nno=#{nno}

mapper.update:
		update notice set
    	ndel = if(ndel=1,0,1)
    	where nno=#{nno}

update members set
m_grade = #{grade}
where m_no = #{m_no}

mapper.select:
select m.m_id, mb.mbtitle, mb.mbcontent, mb.mbdate, mb.mbread, mb.mbdel, m.m_name, sb.b_catename, (select count(*) from multiboard mb left join members m on mb.m_no = m.m_no
and mb.mbboard = #{cate}
and mb.mbtitle like concat('%', #{searchV},'%') and mb.mbcontent like concat('%', #{searchV},'%') and m.m_name like concat('%', #{searchV},'%') and m.m_id like concat('%', #{searchV},'%')
) as count from multiboard mb, members m, setupboard sb
mb.m_no = m.m_no and mb.mbboard = sb.mb_cate and mb.mbboard = #{cate}
and mb.mbtitle like concat('%', #{searchV},'%') and mb.mbcontent like concat('%', #{searchV},'%') and m.m_name like concat('%', #{searchV},'%') and m.m_id like concat('%', #{searchV},'%')
order by mb.mbno desc
